"use client";

import { ArrowRight } from "lucide-react";

import { cn } from "@/lib/utils";

type MicroagentsArrowProps = {
  label?: string;
  direction?: "up" | "right" | "down" | "left";
  className?: string;
};

const rotationByDirection: Record<NonNullable<MicroagentsArrowProps["direction"]>, string> = {
  up: "-rotate-90",
  right: "rotate-0",
  down: "rotate-90",
  left: "rotate-180",
};

export function MicroagentsArrow({
  label = "Route microagent",
  direction = "right",
  className,
}: MicroagentsArrowProps) {
  return (
    <div
      className={cn(
        "inline-flex items-center gap-3 rounded-2xl border border-[color:var(--muted)]/30 bg-[rgba(255,255,255,0.03)] px-4 py-3 text-left shadow-[0_12px_40px_rgba(0,0,0,0.25)]",
        className
      )}
    >
      <div className="flex h-10 w-10 items-center justify-center rounded-xl bg-[color:var(--fg)]/10">
        <ArrowRight
          className={cn("h-4 w-4 text-[color:var(--fg)]", rotationByDirection[direction])}
          aria-hidden
        />
      </div>
      <div className="space-y-1">
        <p className="text-sm font-semibold leading-tight text-[color:var(--fg)]">{label}</p>
        <p className="text-xs text-[color:var(--muted)]">Directional control for linked agents</p>
      </div>
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";

type MicroagentsCircleProps = {
  label?: string;
  status?: "active" | "idle" | "error";
  helperText?: string;
  className?: string;
};

const circlePalette: Record<NonNullable<MicroagentsCircleProps["status"]>, string> = {
  active: "from-[#71f5d5] via-[#7c8bfd] to-[#1f1f2a]",
  idle: "from-[#2a2a2f] via-[#1b1b1f] to-[#0d0d12]",
  error: "from-[#f97316] via-[#f472b6] to-[#1b0f1c]",
};

export function MicroagentsCircle({
  label = "Microagent",
  status = "active",
  helperText = "Online and accepting prompts",
  className,
}: MicroagentsCircleProps) {
  const gradient = circlePalette[status];

  return (
    <div
      className={cn(
        "flex items-center gap-4 rounded-2xl border border-[color:var(--muted)]/30 bg-[rgba(255,255,255,0.02)] px-4 py-3 shadow-[0_12px_40px_rgba(0,0,0,0.25)]",
        className
      )}
    >
      <div
        className={cn(
          "flex h-12 w-12 items-center justify-center rounded-full bg-gradient-to-br",
          gradient,
          status === "active" && "shadow-[0_0_0_6px_rgba(113,245,213,0.08)]"
        )}
        aria-hidden
      >
        <span className="h-[10px] w-[10px] rounded-full bg-black/70 shadow-[0_0_0_6px_rgba(0,0,0,0.45)]" />
      </div>
      <div className="space-y-1">
        <p className="text-sm font-semibold text-[color:var(--fg)]">{label}</p>
        <p className="text-xs text-[color:var(--muted)]">{helperText}</p>
      </div>
    </div>
  );
}

"use client";

